#!/usr/bin/env python3
import re

# Read the file
with open('app/src/main.js', 'r') as f:
    content = f.read()

# Fix specific orphaned objects
fixes = [
    # Fix line 210-212 - orphaned object after recordGameState
    (r'(\s+\}\);
)\s+currentPlayer:[^
]+
\s+gameStatus:[^
]+
\s+\}\);
', r'\1'),
    # Fix line 366-371 - orphaned object
    (r'(
)\s+from,[^
]+
\s+fromCoords,[^
]+
\s+toCoords,[^
]+
\s+movedPiece,[^
]+
\s+capturedPiece,[^
]+
\s+newTurn:[^
]+
\s+\}\);
', r'\1'),
    # Fix line 417-422 - orphaned object after executeBotMove
    (r'(async executeBotMove\(\) \{
)\s+gameMode:[^
]+
\s+isBotTurn:[^
]+
\s+gameStatus:[^
]+
\s+currentPlayer:[^
]+
\s+humanColor:[^
]+
\s+\}\);
', r'\1'),
]

for pattern, replacement in fixes:
    content = re.sub(pattern, replacement, content)

# Write the fixed file
with open('app/src/main.js', 'w') as f:
    f.write(content)

print("Fixed orphaned object properties")

